use codec::{Decode, Encode};
use frame_support::{RuntimeDebug};
use scale_info::TypeInfo;
use sp_finality_grandpa::{AuthorityId, AuthoritySignature};
use sp_runtime::traits::Header as HeaderT;
use sp_std::prelude::*;
use sp_runtime::{
    generic,
    traits::{BlakeTwo256},
};
use std::env;
// default MFV settings
pub type BlockNumber = u32;
pub type Hasher = BlakeTwo256;
pub type Header = generic::Header<BlockNumber, Hasher>;

#[derive(Encode, Decode, RuntimeDebug, TypeInfo, Clone, PartialEq, Eq)]
pub struct GrandpaJustification<Header: HeaderT> {
    pub round: u64,
    pub commit:
        finality_grandpa::Commit<Header::Hash, Header::Number, AuthoritySignature, AuthorityId>,
    pub votes_ancestries: Vec<Header>,
}

fn main() {
    // Example: "";
    let args: Vec<String> = env::args().collect();
    let notification = hex::decode(&args[2]).unwrap_or(vec![0]);

    match args[1].as_str() {
        "blocknumber" => number(notification),
        "authority_set" => authority_set(notification),
        _ => println!("Decoding Error"),
    }
   
}

fn number(arg: Vec<u8>) {
    let decoded =  GrandpaJustification::<Header>::decode(&mut &*arg)
        .map(|justification| {
            justification.commit.target_number
        })
        .map_err(|_| "Decoding Error!");

    match decoded {
        Ok(_) => println!("{:?}", decoded.unwrap()),
        Err(_) => println!("Decoding Error")
    }
}

fn authority_set(arg: Vec<u8>) {
    let decoded =  GrandpaJustification::<Header>::decode(&mut &*arg)
        .map(|justification| {
            let mut auths = vec!();
            for signed in &justification.commit.precommits {
                auths.push(signed.id.encode());
            }
            return auths
        })
        .map_err(|_| "Decoding Error!");

    match decoded {
        Ok(_) => println!("{:}", serde_json::to_string(&decoded.unwrap()).unwrap()),
        Err(_) => println!("Decoding Error")
    }
}